import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import './App.css';
import ProductList from './components/ProductList';
import Login from './components/Login';
import { logout } from './api';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Ki·ªÉm tra xem user ƒë√£ ƒëƒÉng nh·∫≠p ch∆∞a
    const token = localStorage.getItem('auth_token');
    if (token) {
      setIsAuthenticated(true);
      // C√≥ th·ªÉ l·∫•y th√¥ng tin user t·ª´ token ho·∫∑c API
    }
  }, []);

  const handleLoginSuccess = (data) => {
    setIsAuthenticated(true);
    setUser(data.user);
  };

  const handleLogout = () => {
    logout();
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <div className="header-content">
            <h1>üõçÔ∏è E-Commerce App</h1>
            <nav className="nav-menu">
              {isAuthenticated ? (
                <div className="user-menu">
                  <Link to="/products" className="nav-link">S·∫£n ph·∫©m</Link>
                  <span>Xin ch√†o, {user?.name || 'User'}!</span>
                  <button onClick={handleLogout} className="logout-btn">
                    ƒêƒÉng xu·∫•t
                  </button>
                </div>
              ) : (
                <div className="auth-menu">
                  <Link to="/login" className="nav-link">ƒêƒÉng nh·∫≠p</Link>
                </div>
              )}
            </nav>
          </div>
        </header>

        <main className="App-main">
          <Routes>
            <Route 
              path="/" 
              element={
                isAuthenticated ? 
                <Navigate to="/products" replace /> : 
                <Navigate to="/login" replace />
              } 
            />
            <Route 
              path="/login" 
              element={
                isAuthenticated ? 
                <Navigate to="/products" replace /> : 
                <Login onLoginSuccess={handleLoginSuccess} />
              } 
            />
            <Route 
              path="/products" 
              element={
                isAuthenticated ? 
                <ProductList /> : 
                <Navigate to="/login" replace />
              } 
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>

        <footer className="App-footer">
          <p>&copy; 2024 E-Commerce App. ƒê∆∞·ª£c ph√°t tri·ªÉn v·ªõi React + Laravel.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
